import React ,{ useState , useRef} from 'react'
import Product from './Product';

export default function Todo(props){
    const [products,setProducts] = useState([])
    const cmp = useRef(0)
    const name = useRef('')
    const categorie = useRef('')
    const prix = useRef('')
    const quanity = useRef(0)
    const [val,setVal] = useState('')
    
    function setRef(e,value){
        if(e==='name'){
            name.current = value
        }else if(e==='categorie'){
            categorie.current = value
        }else if(e==='prix'){
            prix.current = value
        }else{
            quanity.current = value
        }
        setVal(value)
    }

    function add(){
        const obj = {ref:cmp.current,name:name.current,cat:categorie.current,prix:prix.current,qt:quanity.current}
        cmp.current=cmp.current+1
        setProducts([...products,obj])
        setVal('')
    }

    function Delete(e){ 
        setProducts(products.filter(ee=>ee!==e))
    }

    return (
        <div className='container'>
            <div className="inputs">
                <h1>ADD Product</h1>
                {props.T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" value={val} onChange={(event)=>setRef(e,event.target.value)} className="form-control" placeholder={e}/>
                        <label>{e}</label>
                    </div>
                )}
                <div className="addP">
                    <span onClick={()=>add()} className="add">add</span>
                </div>
            </div>

            <div className="products">
                {products.map((e,i)=>
                    <Product key={i} product={e} Delete={Delete}/>
                )}
            </div>
        </div>
    )
}
