import React ,{ useState , useRef , useContext , useReducer} from 'react'
import { CrudContext } from './App';
import Product from './Product';

export const fContext = React.createContext()
export default function Todo(){
    const [products,setProducts] = useState([])
    const [state, dispatch] = useReducer(reducer, { });
    
    const T = useContext(CrudContext)
    const cmp = useRef(0)
    const name = useRef('')
    const categorie = useRef('')
    const prix = useRef('')
    const quanity = useRef(0)
    
    function setRef(e,value){
        if(e==='name'){
            name.current = value
        }else if(e==='categorie'){
            categorie.current = value
        }else if(e==='prix'){
            prix.current = value
        }else{
            quanity.current = value
        }
    }

    function reducer(state, action) {
        switch (action.type) {
            case 'increment':
            return { count: state.count + 1 };
            case 'decrement':
            return { count: state.count - 1 };
            default:
            throw new Error();
        }
    }

    function Counter() {
        
        return (
            <>
            Count: {state.count}
            <button onClick={() => dispatch({ type: 'increment' })}>+</button>
            <button onClick={() => dispatch({ type: 'decrement' })}>-</button>
            </>
        );
    }


    function add(){
        const obj = {ref:cmp.current,name:name.current,cat:categorie.current,prix:prix.current,qt:quanity.current}
        cmp.current=cmp.current+1
        setProducts([...products,obj])
    }

    function Delete(e){ 
        setProducts(products.filter(ee=>ee!==e))
    }

    return (
        <div className='container'>
            <div className="inputs">
                <h1>ADD Product</h1>
                {T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" onChange={(event)=>setRef(e,event.target.value)} className="form-control" placeholder={e}/>
                        <label>{e}</label>
                    </div>
                )}
                <div className="addP">
                    <span onClick={()=>add()} className="add">add</span>
                </div>
            </div>
            
            <div className="products">
                {products.map((e,i)=>
                    <fContext.Provider value={[e,Delete]}>
                        <Product key={i}/>
                    </fContext.Provider>
                )}
            </div>
        </div>
    )
}
