import React ,{useRef , useContext , useReducer} from 'react'
import { CrudContext } from './App';
import Product from './Product';

export const fContext = React.createContext()
export default function Todo(){
    const T = useContext(CrudContext)
    const [state,dispatch] = useReducer(reducer, {products: []});
    const obj = useRef({cmp:0})
    
    function reducer(state, action) {
        switch (action.type) {
            case 'add':
                const obj2 = action.payload.obj
                return {products: [...state.products,{obj2}]};
            case 'delete':
                return {products: state.products.filter(ee=>ee!==action.payload.e)}
            default:
                throw new Error();
        }
    }

    function setAtt(att,val){
        obj.current[att] = val;
        console.log(obj.current)
    }

    // function add(){
    //     const obj = {name:name.current,cat:categorie.current,prix:prix.current,qt:quanity.current}
    //     dispatch({type:'add'},obj)
    // }

    // function Delete(e){ 
    //     dispatch({type:'Delete'},e)
    // }

    return (
        <div className='container'>
            <div className="inputs">
                <h1>ADD Product</h1>
                {T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" value={obj[e]} className="form-control" placeholder={e} onChange={(event)=>setAtt(e,event.target.value)}/>
                        <label>{e}</label>
                    </div>
                )}
                <div className="addP">
                    <span onClick={()=>dispatch({type:'add',payload:{obj:obj.current}})} className="add">add</span>
                </div>
            </div>
            
            <div className="products">
                {state.products.map((e,i)=>
                    <fContext.Provider value={[e,dispatch]}>
                        <Product key={i}/>
                    </fContext.Provider>
                )}
            </div>
        </div>
    )
}
