    const T = useContext(CrudContext)
    const [newObj,setNewObj] = useState(obj2)
    const oldObj = useRef(obj2)
    const emptyProduct = {}

    function setAtt(att,val){
        newObj
        oldObj.current[att] = val
        setNewObj(oldObj.current)
    }

    return(
        <div className='updateC'>
            {console.log(oldObj.current)}
            <div className="inputs">
                <h1>Update Product</h1>
                {T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" value={newObj[e]} className="form-control" placeholder={e} onChange={(event)=>setAtt(e,event.target.value)}/>
