import React , { useContext , useRef } from 'react'
import { CrudContext } from './App'
import {Link} from 'react-router-dom'

export default function Update({obj2,dispatch}) {
    const T = useContext(CrudContext)
    const obj = useRef(obj2.current)

    function setAtt(att,val){
        obj[att] = val
        console.log(obj)
    }
    return(
        <div className='updateC'>
            {console.log(obj)}
            <div className="inputs">
                <h1>Update Product</h1>
                {T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" value={obj.c[e]} className="form-control" placeholder={e} onChange={(event)=>setAtt(e,event.target.value)}/>
                        <label>{e}</label>
                    </div>
                )}
                <span className="updateP">
                    <Link to='/' className="update2">Update</Link>
                </span>
            </div>
        </div>
    )
}
