import React ,{useRef , useContext , useReducer} from 'react'
import { CrudContext } from './App';
import Product from './Product';

export const fContext = React.createContext()
const initialState = {
    products: []
}
export default function Todo(){
    const T = useContext(CrudContext)
    const [state,dispatch] = useReducer(reducer, initialState);
    let obj = {};
    let cmp = 0;
    
    function reducer(state, action) {
        switch (action.type) {
            case 'add':
                const obj2 = {ref:0,...obj}
                return {products: [...state.products,obj2]};
            case 'delete':
                return {products: state.products.filter(ee=>ee!==action.e)}
            default:
                throw new Error();
        }
    }

    function setAtt(att,val){
        obj[att] = val;
        console.log(obj)
    }

    // function add(){
    //     const obj = {name:name.current,cat:categorie.current,prix:prix.current,qt:quanity.current}
    //     dispatch({type:'add'},obj)
    // }

    // function Delete(e){ 
    //     dispatch({type:'Delete'},e)
    // }

    return (
        <div className='container'>
            <div className="inputs">
                <h1>ADD Product</h1>
                {T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" className="form-control" placeholder={e} onChange={(event)=>setAtt(e,event.target.value)}/>
                        <label>{e}</label>
                    </div>
                )}
                <div className="addP">
                    <span onClick={()=>dispatch({type:'add',})} className="add">add</span>
                </div>
            </div>
            
            <div className="products">
                {state.products.map((e,i)=>
                    <fContext.Provider value={[e,dispatch]}>
                        <Product key={i}/>
                    </fContext.Provider>
                )}
            </div>
        </div>
    )
}
