import React ,{useRef , useContext , useReducer} from 'react'
import { CrudContext } from './App';
import Product from './Product';

export const fContext = React.createContext()

export default function Todo(){
    const T = useContext(CrudContext)
    const [state,dispatch] = useReducer(reducer, {products: []});
    const cmp = useRef(0)
    const name = useRef('')
    const categorie = useRef('')
    const prix = useRef(0)
    const quanity = useRef(0)
    
    function reducer(state, action) {
        switch (action.type) {
            case 'add':
                return {products: [...state.products,action.obj]};
            case 'delete':
                return {products: state.products.filter(ee=>ee!==action.e)}
            default:
                throw new Error();
        }
    }

    function add(){
        const obj = {name:name.current,cat:categorie.current,prix:prix.current,qt:quanity.current}
        cmp.current.cmp=+1
        dispatch({type:'add'},obj)
    }

    function Delete(e){ 
        dispatch({type:'Delete'},e)
    }

    return (
        <div className='container'>
            <div className="inputs">
                <h1>ADD Product</h1>
                {T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" ref={e} className="form-control" placeholder={e}/>
                        <label>{e}</label>
                    </div>
                )}
                <div className="addP">
                    <span onClick={()=>add()} className="add">add</span>
                </div>
            </div>
            
            <div className="products">
                {state.products.map((e,i)=>
                    <fContext.Provider value={[e,Delete]}>
                        <Product key={i}/>
                    </fContext.Provider>
                )}
            </div>
        </div>
    )
}
