        ref.current += 1;
    };

    return (
        <div className='container'>
            <div className="inputs">
                <h1>ADD Product</h1>
                <div className="form-floating my-2">
                    <input type="text" className="form-control" placeholder="name"/>
                    <label>name</label>
                </div>
                <div className="form-floating my-2">
                    <input type="text" className="form-control" placeholder="categorie"/>
                    <label>categorie</label>
                </div>
                <div className="form-floating my-2">
                    <input type="number" className="form-control" placeholder="prix"/>
                    <label>prix</label>
                </div>
                <div className="form-floating my-2">
                    <input type="number" className="form-control" placeholder="quanity"/>
                    <label>quanity</label>
                </div>
                <div className="addP">
                    <span className="add">add</span>
                </div>
            </div>

            <div className="products">
                {products.map((e,i)=>
                    <Product key={i} product={e}/>
                )}
            </div>
        </div>
    )
}
