import React ,{useRef , useContext , useReducer} from 'react'
import { Route, Routes } from 'react-router-dom';
import { CrudContext } from './App';
import Product from './Product';
import Update from './Update';

export const fContext = React.createContext()
function reducer(state, action) {
    switch (action.type) {
        case 'add':
            if(action.payload.cmp.nb===0){
                action.payload.cmp.nb = action.payload.cmp.nb + 1;
                let obj = {cmp:action.payload.cmp.nb,...action.payload.obj}
            }else{
                action.payload.cmp.nb = 0;
            }
            return {products: [...state.products,] , obj2:state.obj2};
        case 'delete':
            return {products: state.products.filter(ee=>ee!==action.payload.e) , obj2:state.obj2};
        case 'getProduct':
            return {products: state.products , obj2: action.payload.e}
        default:
            throw new Error();
    }
}

export default function Home(){
    const T = useContext(CrudContext)
    const [state,dispatch] = useReducer(reducer, { products:[] , obj2:{} });
    const cmp = useRef({nb:0})
    const obj = useRef({})

    function setAtt(att,val){
        obj.current[att] = val
        console.log(obj.current)
    }

    return (
        <div className='container'>
            {console.log(cmp.current.nb)}
            <div className="inputs">
                <h1>ADD Product</h1>
                {T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" value={obj[e]} className="form-control" placeholder={e} onChange={(event)=>setAtt(e,event.target.value)}/>
                        <label>{e}</label>
                    </div>
                )}
                <div className="addP">
                    <span onClick={()=>dispatch({type:'add',payload:{obj:obj.current,cmp:cmp.current}})} className="add">add</span>
                </div>
            </div>
            
            <div className="products">
                {state.products.map((e,i)=>
                    <fContext.Provider key={i} value={[e,dispatch]}>
                        <Product/>
                    </fContext.Provider>
                )}
            </div>
            <Routes>
                <Route path='update' element={<Update obj2={state.obj2} dispatch={dispatch}/>}/>
            </Routes>
        </div>
    )
}
