import React ,{useRef , useContext , useReducer, Fragment} from 'react'
import { Route, Routes } from 'react-router-dom';
import { CrudContext } from './App';
import Product from './Product';
import Update from './Update';

export const fContext = React.createContext()
function reducer(state, action) {
    switch (action.type) {
        case 'add':
            return {products: [...state.products,{cmp:state.cmp,...action.payload.obj}] , cmp:state.cmp+1};
        case 'delete':
            return {products: state.products.filter(ee=>ee!==action.payload.e)}
        default:
            throw new Error();
    }
}

export default function Home(){
    const T = useContext(CrudContext)
    const [state,dispatch] = useReducer(reducer, {products:[],cmp:0});
    const obj = useRef({})
    const obj2 = useRef({})

    function getProduct(obj){
        obj2.current = obj
        console.log(obj)
    }

    function setAtt(att,val){
        obj.current[att] = val
        console.log(obj.current)
    }

    return (
        <Fragment>
        <div className='container'>
            <div className="inputs">
                <h1>ADD Product</h1>
                {T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" value={obj[e]} className="form-control" placeholder={e} onChange={(event)=>setAtt(e,event.target.value)}/>
                        <label>{e}</label>
                    </div>
                )}
                <div className="addP">
                    <span onClick={()=>dispatch({type:'add',payload:{obj:obj.current}})} className="add">add</span>
                </div>
            </div>
            
            <div className="products">
                {state.products.map((e,i)=>
                    <fContext.Provider key={i} value={[e,dispatch,getProduct]}>
                        <Product/>
                    </fContext.Provider>
                )}
            </div>
        </div>
            <Routes>
                <Route path='update' element={<Update obj2={obj2} dispatch={dispatch}/>}/>
            </Routes>
        </Fragment>
    )
}
