import React ,{useRef , useContext , useReducer} from 'react'
import { CrudContext } from './App';
import Product from './Product';

export const fContext = React.createContext()

export default function Todo(){
    const T = useContext(CrudContext)
    const [state,dispatch] = useReducer(reducer, {products: []});
    const objRef = useRef({})
    // let cmp = 0
    // let name = ''
    // let categorie = ''
    // let prix = 0
    // let quanity = 0
    
    function reducer(state, action) {
        switch (action.type) {
            case 'add':
                objRef.current = {cmp:0,name:'',categorie:'',prix:0,quanity:0}
                const obj = {ref:objRef.current.cmp,name:objRef.current.name,cat:objRef.current.categorie,prix:objRef.current.prix,qt:objRef.current.quanity}
                objRef.current.cmp+=1;
                return {products: [...state.products,obj]};
            case 'delete':
                return {products: state.products.filter(ee=>ee!==action.e)};
            default:
                throw new Error();
        }
    }
    
    function setRef(e,value){
        if(e==='name'){
            objRef.current['name'] = value
        }else if(e==='categorie'){
            objRef.current.categorie = value
        }else if(e==='prix'){
            objRef.current.prix = value
        }else{
            objRef.current.quanity = value
        }
    }

    // function add(){
    //     const obj = {ref:cmp.current,name:name.current,cat:categorie.current,prix:prix.current,qt:quanity.current}
    //     cmp.current=cmp.current+1
    //     setProducts([...products,obj])
    // }

    // function Delete(e){ 
    //     setProducts(products.filter(ee=>ee!==e))
    // }

    return (
        <div className='container'>
            <div className="inputs">
                <h1>ADD Product</h1>
                {T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" onChange={(event)=>setRef(e,event.target.value)} className="form-control" placeholder={e}/>
                        <label>{e}</label>
                    </div>
                )}
                <div className="addP">
                    <span onClick={()=>dispatch({type:'add'})} className="add">add</span>
                </div>
            </div>
            
            <div className="products">
                {state.products.map((e,i)=>
                    <fContext.Provider value={[e,dispatch]}>
                        <Product key={i}/>
                    </fContext.Provider>
                )}
            </div>
        </div>
    )
}
