import React ,{useRef , useContext , useReducer} from 'react'
import { CrudContext } from './App';
import Product from './Product';

export const fContext = React.createContext()
export default function Todo(){
    const T = useContext(CrudContext)
    const [state,dispatch] = useReducer(reducer, {products:[],cmp:0});
    const obj = useRef({})
    
    function reducer(state, action) {
        switch (action.type) {
            case 'add':
                return {products: [...state.products,{cmp:state.cmp,...action.payload.obj}] , cmp:state.cmp+1};
            case 'delete':
                return {products: state.products.filter(ee=>ee!==action.payload.e)}
            default:
                throw new Error();
        }
    }

    function setAtt(att,val){
        obj.current[att] = val
    }

    return (
        <div className='container'>
            <div className="inputs">
                <h1>ADD Product</h1>
                {T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" value={obj[e]} className="form-control" placeholder={e} onChange={(event)=>setAtt(e,event.target.value)}/>
                        <label>{e}</label>
                    </div>
                )}
                <div className="addP">
                    <span onClick={()=>dispatch({type:'add',payload:{obj:obj.current}})} className="add">add</span>
                </div>
            </div>
            
            <div className="products">
                {state.products.map((e,i)=>
                    <fContext.Provider value={dispatch}>
                        <Product key={i}/>
                </fContext.Provider>
                )}
            </div>
        </div>
    )
}
