import React , { useContext , Link} from 'react'
import { CrudContext } from './App'

export default function Update() {
    const T = useContext(CrudContext)
    let obj = {}

    function setAtt(att,val){
        obj[att] = val
        console.log(obj)
    }
    return(
        <div>
            <div className="inputs">
                <h1>Update Product</h1>
                {T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" value={obj[e]} className="form-control" placeholder={e} onChange={(event)=>setAtt(e,event.target.value)}/>
                        <label>{e}</label>
                    </div>
                )}
                <span to='/update' className="updateP">
                    <span className="update">Update</span>
                </span>
            </div>
        </div>
    )
}
