import React ,{useRef , useContext , useReducer} from 'react'
import { CrudContext } from './App';
import Product from './Product';

export const fContext = React.createContext()

export default function Todo(){
    const T = useContext(CrudContext)
    const [state,dispatch] = useReducer(reducer, {products: []});
    const objRef = useRef({cmp:0})
    // let cmp = 0
    // let name = ''
    // let categorie = ''
    // let prix = 0
    // let quanity = 0
    
    function reducer(state, action) {
        switch (action.type) {
            case 'add':
                return add();
            case 'delete':
                return <Delete></Delete>
            default:
                throw new Error();
        }
    }
    
    function setRef(e,value){
        objRef.current[e] = value
        console.log(objRef.current)
    }

    function add(){
        const obj = {ref:objRef.current.cmp,...objRef}
        objRef.current.cmp=+1
        return {products: [...state.products,obj]}
    }

    function Delete(e){ 
        return {products: state.products.filter(ee=>ee!==e)}
    }

    return (
        <div className='container'>
            <div className="inputs">
                <h1>ADD Product</h1>
                {T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" onChange={(event)=>setRef(e,event.target.value)} className="form-control" placeholder={e}/>
                        <label>{e}</label>
                    </div>
                )}
                <div className="addP">
                    <span onClick={()=>dispatch({type:'add'})} className="add">add</span>
                </div>
            </div>
            
            <div className="products">
                {state.products.map((e,i)=>
                    <fContext.Provider value={[e,dispatch]}>
                        <Product key={i}/>
                    </fContext.Provider>
                )}
            </div>
        </div>
    )
}
