import React ,{ useState , useRef} from 'react'
import Product from './Product';

export default function Todo(props){
    const [products,setProducts] = useState([])
    const cmp = useRef(0)
    const nameR = useRef('')
    const categorieR = useRef('')
    const prixR = useRef('')
    const quanityR = useRef(0)
    
    function setRef(e,value){
        if(e==='name'){
            nameR.current = value
        }else if(e==='categorie'){
            categorieR.current = value
        }else if(e==='prix'){
            prixR.current = value
        }else{
            quanityR.current = value
        }
    }

    function add(){
        const obj = {ref:cmp,name:nameR,cat:categorieR,prix:prixR,qt:quanityR}
        cmp.current=cmp.current+1
        setProducts([...products,{...obj}])
    }

    return (
        <div className='container'>
            {console.log('render')}
            <div className="inputs">
                <h1>ADD Product</h1>
                {props.T.map((e,i)=>
                    <div key={i} className="form-floating my-2">
                        <input type="text" onChange={(event)=>setRef(e,event.target.value)} className="form-control" placeholder={e}/>
                        <label>{e}</label>
                    </div>
                )}
                <div className="addP">
                    <span onClick={()=>add()} className="add">add</span>
                </div>
            </div>

            <div className="products">
                {products.map((e,i)=>
                    <Product key={i} product={e}/>
                )}
            </div>
        </div>
    )
}
